import SVGBackgroun from "../assets/SVGBackgroun";

export default function FeaturesMatter() {
  return (
    <section className="relative bg-[#EAF7FF] overflow-hidden pt-12 md:pt-16 md:pb-16 lg:py-20 px-4 md:px-8">
      <SVGBackgroun />

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Header */}
        <div className="text-center mb-8 md:mb-12">
          <h2 className="text-3xl md:text-4xl lg:text-[56px] font-bold text-brand-dark leading-tight lg:leading-[62.72px] tracking-[-0.02em] mb-4 md:mb-6 px-4">
            Why These Features Matter
          </h2>
          <p className="text-base md:text-lg lg:text-[17px] text-brand-gray leading-relaxed lg:leading-[24px] max-w-2xl mx-auto px-4">
            SmartNotes AI isn't about ticking checkboxes. Every feature was
            designed based on real conversations with doctors, billing experts,
            and healthcare admins.
          </p>
        </div>

        {/* Main Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 md:gap-8 items-center mx-auto">
          {/* Left Column - Feature Cards */}
          <div className="space-y-6 flex flex-col">
            <FeatureCard
              icon={<LightbulbIcon />}
              title="Save upto 10+ Hours/week on documentation"
            />
            <FeatureCard
              icon={<BoltIcon />}
              title="Reduce burnout by automating repetitive tasks"
            />
          </div>
          {/* Right Column - Feature Cards */}
          <div className="space-y-6 lg:ml-auto flex flex-col">
            <FeatureCard
              icon={<TargetIcon />}
              title="Improve billing accuracy and reduce revenue leakage"
            />
            <FeatureCard
              icon={<CollaborateIcon />}
              title="Collaborate better with teams and specialists"
            />
          </div>
        </div>

        {/* Bottom Feature Card - Mobile/Tablet */}
        <div className="mt-6 flex justify-center lg:hidden">
          <FeatureCard
            icon={<HeartIcon />}
            title="Gain back headspace to focus on patient care"
          />
        </div>

        {/* Bottom Feature Card - Desktop */}
        <div className="hidden lg:flex justify-center mt-8">
          <div className="w-full max-w-[269px]">
            <FeatureCard
              icon={<HeartIcon />}
              title="Gain back headspace to focus on patient care"
            />
          </div>
        </div>
      </div>
      <img
        src="/images/FeaturesMatter.png?width=1152"
        alt="Phone background"
        className="md:absolute static bottom-0 md:-translate-x-1/2 left-1/2 md:w-[80%] w-full h-full md:h-[80%] object-contain md:mt-0 mt-5"
      />
    </section>
  );
}

// Feature Card Component
function FeatureCard({ icon, title }) {
  return (
    <div
      className="inline-flex flex-col justify-center items-start gap-5 p-8 rounded-2xl border border-white md:max-w-[269px] w-full backdrop-blur-[11.5px]"
      style={{
        background:
          "linear-gradient(100deg, #FFF 29.18%, rgba(255, 255, 255, 0.10) 100%)",
      }}
    >
      <div className="w-11 h-11 rounded-full bg-white border border-[#6ED2FD]/10 flex items-center justify-center flex-shrink-0">
        {icon}
      </div>
      <h4 className="text-brand-dark-alt text-lg md:text-[19px] font-bold leading-6">
        {title}
      </h4>
    </div>
  );
}

